import random
secret_number = random.randint(1, 10)

guess = int(input("Guess a number between 1 and 10: "))

if guess == secret_number:
    print("Congratulations! You guessed it!")
elif guess < secret_number:
    print("Too low! The number was", secret_number)
else:
    print("Too high! The number was", secret_number)

import math

def area_of_circle(radius):
    return math.pi * radius ** 2

def celsius_to_fahrenheit(celsius):
    return (celsius * 9/5) + 32

def factorial(n):
    if n == 0:
        return 1
    return n * factorial(n-1)

# Test your functions
print("Area of circle with radius 5:", area_of_circle(5))
print("25°C in Fahrenheit:", celsius_to_fahrenheit(25))
print("Factorial of 5:", factorial(5))

# Create a dictionary to store student grades
students = {}

def add_student(name, grade):
    students[name] = grade

def get_grade(name):
    return students.get(name, "Student not found")

def calculate_average():
    if not students:
        return 0
    return sum(students.values()) / len(students)

# Add some sample data
add_student("Alice", 85)
add_student("Bob", 92)
add_student("Charlie", 78)

print("Alice's grade:", get_grade("Alice"))
print("Class average:", calculate_average())

def linear_search(numbers, target):
    for i in range(len(numbers)):
        if numbers[i] == target:
            return i
    return -1

# Test your function
numbers = [4, 2, 7, 1, 9, 3, 6]
print(linear_search(numbers, 7))  # Should return 2
print(linear_search(numbers, 5))  # Should return -1

